package com.echo.domain;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;@Data@Builder@NoArgsConstructor@AllArgsConstructorpublic class Message {    private String conversationId;    private String content;    private String role;    public Message(String role, String content) {        this.role = role;        this.content = content;    }    public String getRole() {        return role;    }    public void setRole(String role) {        this.role = role;    }    public String getConversationId() {        return conversationId;    }    public void setConversationId(String conversationId) {        this.conversationId = conversationId;    }    public String getContent() {        return content;    }    public void setContent(String content) {        this.content = content;    }}